"use strict";function Slider(e){const t=document.querySelector(e),r=document.querySelectorAll(e+"__item"||e+" li"),l=document.querySelector(e+"__prev"),n=document.querySelector(e+"__next");let s=t.scrollWidth/r.length,c=0;n.addEventListener("click",()=>{c<r.length-1?c++:c=0,t.style.transform=`translateX(-${c*s}px)`}),l.addEventListener("click",()=>{c>0?c--:c=r.length-1,t.style.transform=`translateX(-${c*s}px)`}),window.addEventListener("resize",()=>{s=t.scrollWidth/r.length,t.style.transform=`translateX(-${c*s}px)`})}const slider=new Slider(".slider");